--
-- Name: camelize(character varying); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION camelize(_str character varying) RETURNS character varying
    LANGUAGE sql IMMUTABLE
    AS $$
  select string_agg(replace((upper(substring(str from 1 for 1)) || substring(str from 2)), 'Xxx', ''), '') from unnest(string_to_array('xxx' || _str, '_')) as str;
$$;


--
-- Name: column_ddl(character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION column_ddl(column_name character varying, pg_type character varying, min character varying, max character varying) RETURNS character varying
    LANGUAGE sql IMMUTABLE
    AS $$
    SELECT '"' || fhir.underscore(column_name) || '" ' || pg_type
      || case
        when max = '*' then '[]'
        else ''
      end
      || case
        when min = '1' then ' not null'
        else ''
      end;
$$;


