CREATE FUNCTION insert_operation_outcome(_data json, _container_id uuid DEFAULT NULL::uuid, _resource_id uuid DEFAULT NULL::uuid) RETURNS TABLE(resource_id uuid, id uuid, path text[], container_id uuid, parent_id uuid, value json)
    LANGUAGE sql
    AS $$
        WITH 
       _operation_outcome  AS (
