--
-- Name: resource_tables; Type: VIEW; Schema: meta; Owner: -
--

CREATE VIEW resource_tables AS
         SELECT e.path,
            fhir.table_name(e.path) AS table_name,
            fhir.resource_table_name(e.path) AS resource_table_name,
            fhir.parent_table_name(e.path) AS parent_table_name,
                CASE
                    WHEN (array_length(e.path, 1) > 1) THEN 'resource_component'::text
                    ELSE 'resource'::text
                END AS base_table,
            COALESCE(( SELECT array_agg(rc.column_ddl) AS array_agg
                   FROM resource_columns rc
                  WHERE ((fhir.array_pop(rc.path) = e.path) AND ((rc.schema)::text = (e.schema)::text))), ARRAY[]::character varying[]) AS columns,
            e.min,
            e.max,
            e.schema,
            'fhir'::text AS parent_schema
           FROM compound_resource_elements e
UNION
         SELECT expanded_with_dt_resource_elements.path,
            fhir.table_name(expanded_with_dt_resource_elements.path) AS table_name,
            fhir.resource_table_name(expanded_with_dt_resource_elements.path) AS resource_table_name,
            fhir.parent_table_name(expanded_with_dt_resource_elements.path) AS parent_table_name,
            expanded_with_dt_resource_elements.base_table,
            ARRAY[]::character varying[] AS columns,
            expanded_with_dt_resource_elements.min,
            expanded_with_dt_resource_elements.max,
            expanded_with_dt_resource_elements.schema,
            'fhir'::text AS parent_schema
           FROM expanded_with_dt_resource_elements;


--
-- Name: resources_ddl; Type: VIEW; Schema: meta; Owner: -
--

CREATE VIEW resources_ddl AS
 SELECT ARRAY[((((((((((('CREATE TABLE'::text || ' "'::text) || (resource_tables.schema)::text) || '"."'::text) || (resource_tables.table_name)::text) || '"'::text) || '('::text) || array_to_string(resource_tables.columns, ','::text)) || ')'::text) || ' INHERITS (fhir.'::text) || resource_tables.base_table) || ')'::text), (((((('ALTER TABLE '::text || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' ALTER COLUMN _type SET DEFAULT $$'::text) || (resource_tables.table_name)::text) || '$$'::text), (((('ALTER TABLE '::text || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' ADD PRIMARY KEY (_id)'::text),
        CASE
            WHEN (resource_tables.base_table = 'resource'::text) THEN (((('CREATE INDEX ON '::text || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' (container_id);'::text)
            ELSE ((((((((((((((((((((((((('ALTER TABLE '::text || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' ADD FOREIGN KEY (resource_id) REFERENCES fhir.'::text) || (resource_tables.resource_table_name)::text) || ' (_id) ON DELETE CASCADE DEFERRABLE;'::text) || 'CREATE INDEX ON '::text) || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' (resource_id);'::text) || 'ALTER TABLE '::text) || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' ADD FOREIGN KEY (parent_id) REFERENCES '::text) || resource_tables.parent_schema) || '.'::text) || (resource_tables.parent_table_name)::text) || ' (_id) ON DELETE CASCADE DEFERRABLE;'::text) || 'CREATE INDEX ON '::text) || (resource_tables.schema)::text) || '.'::text) || (resource_tables.table_name)::text) || ' (parent_id);'::text)
        END] AS ddls
   FROM resource_tables
  WHERE ((resource_tables.table_name)::text !~ '^profile'::text);


SET search_path = public, pg_catalog;

--
-- Name: fhir_columns; Type: MATERIALIZED VIEW; Schema: public; Owner: -; Tablespace: 
--

CREATE MATERIALIZED VIEW fhir_columns AS
 SELECT columns.column_name,
    columns.data_type,
    columns.table_name
   FROM information_schema.columns
  WHERE ((columns.table_schema)::text = 'fhir'::text)
  WITH NO DATA;


--
-- Name: insert_ctes; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW insert_ctes AS
 SELECT resource_tables.path,
        CASE
            WHEN (array_length(resource_tables.path, 1) = 1) THEN fhir.eval_template('
       _{{table_name}}  AS (
         SELECT uuid as resource_id, uuid, path, _container_id as container_id, null::uuid as parent_id, value
            FROM (
              SELECT coalesce(_resource_id, uuid_generate_v4()) as uuid , ARRAY[''{{resource}}''] as path, _data as value
         ) _
      )
     '::text, VARIADIC ARRAY['resource'::character varying, resource_tables.path[1], 'table_name'::character varying, resource_tables.table_name])
            ELSE fhir.eval_template('
         _{{table_name}}  AS (
           SELECT
             p.resource_id as resource_id,
             uuid_generate_v4() as uuid,
             {{path}}::text[] as path,
             null::uuid as container_id,
             p.uuid as parent_id,
             {{value}} as value
           FROM _{{parent_table}} p
           WHERE p.value IS NOT NULL
        )
        '::text, VARIADIC ARRAY['table_name'::character varying, resource_tables.table_name, 'path'::character varying, (quote_literal((resource_tables.path)::text))::character varying, 'value'::character varying, (fhir.json_extract_value_ddl(resource_tables.max, fhir.array_last(resource_tables.path)))::character varying, 'parent_table'::character varying, fhir.table_name(fhir.array_pop(resource_tables.path))])
        END AS cte
   FROM meta.resource_tables
  ORDER BY resource_tables.path;


--
-- Name: insert_ddls; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW insert_ddls AS
 SELECT insert_ctes.path[1] AS resource,
    fhir.eval_template('
     --DROP FUNCTION IF EXISTS fhir.insert_{{fn_name}}(json, uuid);
     CREATE OR REPLACE FUNCTION fhir.insert_{{fn_name}}(_data json, _container_id uuid default null, _resource_id uuid default null)
     RETURNS TABLE(resource_id uuid, id uuid, path text[], container_id uuid, parent_id uuid, value json) AS
     $fn$
        WITH {{ctes}}
        {{selects}};
     $fn$
     LANGUAGE sql;
  '::text, VARIADIC ARRAY['fn_name'::character varying, fhir.underscore(insert_ctes.path[1]), 'ctes'::character varying, (array_to_string(array_agg(insert_ctes.cte ORDER BY insert_ctes.path), ',
'::text))::character varying, 'selects'::character varying, (string_agg(('SELECT * FROM _'::text || (fhir.table_name(insert_ctes.path))::text), '
 UNION ALL '::text))::character varying]) AS ddl
   FROM insert_ctes
  GROUP BY insert_ctes.path[1]
 HAVING ((insert_ctes.path[1])::text <> 'Profile'::text);


